const DiaryItem = ({
  id,
  author,
  content,
  emotion,
  created_date,
  onDelete,
}) => {
  return (
    <div className="DiaryItem">
      <div className="info">
        <span>
          Writer : {author} | Emotional score : {emotion}
        </span>
        <br />
        <span className="date">
          {new Date(created_date).toLocaleString("en-US")}
        </span>
      </div>
      <div className="content">{content}</div>
      <button
        onClick={() => {
          if (window.confirm(`${id}번째 일기를 정말 삭제 하시겠습니까?`)) {
            onDelete(id);
          }
        }}
      >
        삭제하기
      </button>
    </div>
  );
};

export default DiaryItem;
